import { async } from "@firebase/util";
import React, { useState } from "react";
import { createAuthUserWhitEmailAndPwd } from "../../utils/firebase";

const defaultFormField = {
  DispalyName: "",
  Email: "",
  Password: "",
  ConfirmPassword: "",
};

const SingUpForm = () => {
  const [formField, setFormField] = useState(defaultFormField);
  const { DispalyName, Email, Password, ConfirmPassword } = formField;

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setFormField({ ...formField, [name]: value });
  };

  const submiHandler = async (event) => {
    event.preventDefault();

    if (Password != ConfirmPassword) {
      alert("Password dosn't match");
      return;
    }

    try {
      const response = await createAuthUserWhitEmailAndPwd(Email, Password);
      console.log(response);

      await creatUserDocumentFromAuth(response, { DispalyName });
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <h1>Sing Up</h1>
      <form onSubmit={submiHandler}>
        <label>Dispaly Name</label>
        <input
          type='text'
          required
          name='DispalyName'
          value={DispalyName}
          onChange={changeHandler}
        />

        <label>Email</label>
        <input
          type='email'
          required
          name='Email'
          value={Email}
          onChange={changeHandler}
        />

        <label>Password</label>
        <input
          type='password'
          required
          name='Password'
          value={Password}
          onChange={changeHandler}
        />

        <label>Confirm Password</label>
        <input
          type='password'
          required
          name='ConfirmPassword'
          value={ConfirmPassword}
          onChange={changeHandler}
        />
        <button type='submit'>Sing Up</button>
      </form>
    </div>
  );
};

export default SingUpForm;
